{
   editor: function(options) {
      // options = {
      //   node: the node into which you should render the editor
      //   entry = JSON object representing the entry
      //   readOnly = true if we want the entry to be uneditable
      //   onChange = method to call if data changes
      //   onValidChange - method to call if data state changes from valid to invalid or vice versa
      // }
      let nvh = "entry: house-n\n  lemma: house\n  pos: n\n  postitle: noun\n  wrong_lemma: \n  pronunciation: \n  syllabification: \n  sense: \n    example: \n  candidate_example: example candidate 1\n  candidate_example: example candidate 2\n  candidate_example: example candidate 3\n  candidate_example: example candidate 4\n  candidate_example: example candidate 5\n  candidate_example: example candidate 6\n  candidate_example: example candidate 7\n  candidate_example: example candidate 8\n  candidate_example: example candidate 9"
      // options.entry = window.nvhStore.nvhToJson(nvh)
      this.options = options
      this.node = $(options.node)
      this.entry = options.entry
      this.contentNode = $('<div class="myEditorContentNode"></div>').appendTo(this.node)
      this.isValid = null
      this.lang = "id" //en
      this._createCandidateListTemplate()
      this._calculateExpandedSenseMoreInfo()
      this._render()
      this._validate()
   },

   getValue: function() {
      if(this.isValid && !this._getChildElement(this.entry, "__lexonomy_complete")){
         let lexonomyCompleteElement = this._addChildElement(this.entry, "__lexonomy_complete")
         lexonomyCompleteElement.value = 1         
      }
      return this.entry
   },

   update: function(entry) {
      let nvh = "entry: house-n\n  lemma: house\n  pos: n\n  postitle: noun\n  wrong_lemma: \n  pronunciation: \n  syllabification: \n  sense: some sense\n    example: \n  candidate_example: example candidate 1\n  candidate_example: example candidate 2\n  candidate_example: example candidate 3\n  candidate_example: example candidate 4\n  candidate_example: example candidate 5\n  candidate_example: example candidate 6\n  candidate_example: example candidate 7\n  candidate_example: example candidate 8\n  candidate_example: example candidate 9"
      // entry = window.nvhStore.nvhToJson(nvh)
      this.entry = entry
      this._createCandidateListTemplate()
      this._calculateExpandedSenseMoreInfo()
      this._render()
      this._validate()
   },


   _render: function() {
      this.contentNode.empty()
      this._renderErrorMessageBar()
      this._renderHeader()
      if (!this._getChildValue(this.entry, "wrong_lemma")) {
         this._renderHeadwordForm()
         this._renderSenses()
      } else {
         this._renderWrongLemmaWarning()
      }
   },

   _renderErrorMessageBar: function() {
      $(`
         <div id="errorMessageBar"
               class="errorMessageBar red lighten-4 pt-2 pb-2 pl-4 pr-4"
               style="${this.error ? '' : 'display: none;'}">
            <div id="errorMessage"
               class="errorMessage">
               ${this.error}
            </div>
         </div>`).appendTo(this.contentNode)
   },

   _renderHeader: function() {
      let lemma = this._getChildValue(this.entry, "lemma")
      let postitle = this._getChildValue(this.entry, "postitle")
      let wrong_lemma = this._getChildValue(this.entry, "wrong_lemma")
      $(`
         <div class="displayFlex alignCenter borderBottom positionRelative">
            <span>
               <h1 class="inlineBlock">${lemma}</h1>
               <span class="ml-2 grey-text">${postitle}</span>
            </span>
            <div class="ml-auto">
               <label>
                  <input id="wrong_lemma"
                        type="checkbox"
                        ${wrong_lemma ? 'checked' : ''}/>
                  <span>${this._("entryIsWrong")}</span>
               </label>
            </div>
            <small class="langToggle">
               ${this.lang == "en" ? "<span id=\"langToggle\">id</span>" : "id"}
               |
               ${this.lang == "id" ? "<span id=\"langToggle\">en</span>" : "en"}
            </small>
         </div>`).appendTo(this.contentNode)
      $("#wrong_lemma").change(this._onWrong_lemmaChange.bind(this))
      $("#langToggle").click(this._onLangToggleClick.bind(this))
   },

   _renderWrongLemmaWarning: function() {
      $(`<div class="wrongLemmaWarning">${this._("wrongLemma")}</div>`).appendTo(this.contentNode)
   },

   _renderHeadwordForm: function() {
      let headwordtype = this._getChildValue(this.entry, "headwordtype")      
      let headwordtypeOptions = this.const.headwordtype.map(t => `<option value="${t[0]}" ${t[0] == headwordtype ? 'selected' : ''}>${t[1]}</option>`).join("\n")
      $(`
         <div class="headwordForm">
            <div class="displayFlex">
               <div class="input-field">
                  <select id="headwordtype">
                     ${headwordtypeOptions}
                  </select>
                  <label>${this._("headwordType")}</label>
               </div>              
            </div>            
         </div>`).appendTo(this.contentNode)      
      $("#headwordtype").formSelect()
         .on("change", this._onHeadwordtypeChange.bind(this))
   },

   _renderSenses: function() {
      let senseContainer = $(`<div class="senses"></div>`).appendTo(this.contentNode)
      $(`<h3>${this._("senses")}</h3>`).appendTo(senseContainer)
      let senses = this._getChildElements(this.entry, "sense")
      senses.forEach((sense, idx) => {
         let example = this._getChildValue(sense, "example")
         let is_sense_figurative = this._getChildValue(sense, "is_sense_figurative", false)
         let is_example_figurative = this._getChildValue(sense, "is_example_figurative", false)         
         let baseword = this._getChildValue(sense, "baseword")
         let homonym_number = this._getChildValue(sense, "homonym_number", "")
         let syllabification = this._getChildValue(sense, "syllabification")
         let pronunciation = this._getChildValue(sense, "pronunciation")
         let senseNode = $(`
            <div class="sense card-panel grey lighten-5 ml-8 mr-16 mb-8 positionRelative">
               <span class="senseNum">${idx + 1}.</span>
               <div class="displayFlex alignBaseline">
                  <div class="input-field mt-0 senseDefinition">
                     <input name="sense"
                           value="${sense.value}"/>
                     <label>${this._("sense")}</label>
                  </div>
                  <label class="ml-4 figurativeCheckbox">
                     <input type="checkbox"
                           name="is_sense_figurative"
                        ${is_sense_figurative ? 'checked' : ''}/>
                     <span>${this._("isSenseFigurative")}</span>
                  </label>
               </div>
               <button class="removeSenseBtn btn btn-floating ${idx == 0 ? 'hide' : ''}">
               <i class="material-icons">delete</i>
               </button>
               <div class="displayFlex">
                  <div class="input-field">
                     <input id="pronunciation"
                           type="text"
                           name="pronunciation"
                           value="${pronunciation}"/>
                     <label>${this._("pronunciation")}</label>
                  </div>
                  <div class="input-field ml-4">
                     <input id="syllabification"
                           type="text"
                           name="syllabification"
                           value="${syllabification}"/>
                     <label>${this._("syllabification")}</label>
                  </div>
                  <div class="input-field ml-4">
                     <input id="baseword"
                           type="text"
                           name="baseword"
                           value="${baseword}"/>
                     <label>${this._("baseword")}</label>
                  </div>
                  <div class="input-field ml-4">
                     <input id="homonym_number"
                           type="number"
                           min="0"
                           name="homonym_number"
                           value="${homonym_number}"/>
                     <label>${this._("homonymNum")}</label>
                  </div>
               </div>
               <div class="example displayFlex alignBaseline">
                  <div class="input-field mb-0 exampleInput">
                     <input name="example"
                           placeholder="${this._("chooseFromListBelow")}"
                           value="${example}"
                           ${example ? '' : 'disabled'}/>
                     <label>${this._("example")}</label>
                     <span class="helper-text">${this._("chooseFromListBelowOr")} <a href="javascript:void(0);" class="addOwnExample">${this._("addYourOwn")}</a>. ${this._("youCanEdit")}.</span>
                  </div>
                  <label class="ml-4 figurativeCheckbox">
                     <input type="checkbox"
                           name="is_example_figurative"
                        ${is_example_figurative ? 'checked' : ''}/>
                     <span>${this._("isExampleFigurative")}</span>
                  </label>
               </div>
               <div class="candidateList white p-2">
                  ${this.exampleCandidateTemplate}
               </div>
               <div class="borderTop moreInfoContainer mt-6 pt-4">
                  <label class="moreInfoToggle">
                     <input type="checkbox"
                        ${this.expandedSenses[idx] ? 'checked' : ''}/>
                     <span>${this._("addMoreInformation")}</span>
                  </label>
                  <div class="moreInfoForm mt-4" style="${this.expandedSenses[idx] ? '' : 'display:none;'}">
                     ${this._getMoreInformationForm(sense)}
                     <div></div>
                     <div></div>
                     <div></div>
                     <div></div>
                     <div></div>
                     <div></div>
                  </div>
               </div>
            </div>`).appendTo(senseContainer)
         senseNode.find(".removeSenseBtn").click(this._onRemoveSenseClick.bind(this, sense, idx))
         senseNode.find("input[name=\"sense\"]").on("input", this._onSenseInput.bind(this, sense))
         senseNode.find("input[name=\"homonym_number\"]").on("input", this._onHomonymNumberInput.bind(this, sense))
         senseNode.find("input[name=\"is_sense_figurative\"]").on("change", this._onIsFigurativeChange.bind(this, sense))
         senseNode.find("input[name=\"is_example_figurative\"]").on("change", this._onIsFigurativeChange.bind(this, sense))         
         senseNode.find(".addOwnExample").click(this._onAddOwnExampleClick.bind(this))
         senseNode.find("#baseword").on("input", this._onInput.bind(this, sense))
         senseNode.find("#pronunciation").on("input", this._onInput.bind(this, sense))
         senseNode.find("#syllabification").on("input", this._onInput.bind(this, sense))
         senseNode.find("input[name=\"example\"]").on("input", this._onInput.bind(this, sense))
         senseNode.find(".candidate").click(this._onSelectCandidateClick.bind(this, sense))
         senseNode.find(".moreInfoToggle").change(this._onMoreInfoToggleClick.bind(this, idx))
         senseNode.find("input[name=\"scientificname\"]").on("input", this._onSenseInput.bind(this, sense))
         senseNode.find("input[name=\"chemicalformula\"]").on("input", this._onSenseInput.bind(this, sense))
         senseNode.find("select").formSelect().on("change", this._onSelectChange.bind(this, sense))
         senseNode.find(".customValueToggleIcon").on("click", this._onCustomValueToggleClick.bind(this, sense))
         senseNode.find(".customValueInput").on("input", this._onInput.bind(this, sense))
      })
      $(`<button class="addSenseButton btn ml-8">
            <i class="material-icons left">add</i>
            ${this._("addSense")}
         </button>`)
            .appendTo(senseContainer)
            .click(this._onAddSenseClick.bind(this))
   },

   _onLangToggleClick(){
      this.lang = this.lang == "en" ? "ind" : "en"
      this._render()
   },

   _onInput: function(parent, evt) {
      let element = this._getOrCreateChildElement(parent, evt.target.name)
      element.value = evt.target.value
      this._validate()
   },

   _onSelectChange: function(parent, evt) {
      let element = this._getOrCreateChildElement(parent, evt.target.name)
      element.value = evt.target.value
      this._validate()
   },

   _onHeadwordtypeChange: function(evt) {
      let headwordtypeElement = this._getOrCreateChildElement(this.entry, "headwordtype")
      headwordtypeElement.value = evt.target.value
   },

   _onWrong_lemmaChange: function(evt) {
      let wrong_lemmaElement = this._getOrCreateChildElement(this.entry, "wrong_lemma")
      wrong_lemmaElement.value = evt.target.checked ? "1" : ""
      this._render()
      this._validate()
   },

   _onRemoveSenseClick: function(sense, idx, evt) {
      this.entry.children = this.entry.children.filter(s => s != sense)
      this.expandedSenses.splice(idx, 1)
      this._render()
   },

   _onAddSenseClick: function(evt) {
      let senseElement = this._createChildElement(this.entry, "sense")
      let firstSense = this._getChildElements(this.entry, "sense")[0] 
      ;["baseword", "pronunciation", "syllabification", "homonym_number"].forEach(elementName => {
         elementValue = this._getChildValue(firstSense, elementName)
         if(elementValue !== ""){
            element = this._getOrCreateChildElement(senseElement, elementName)
            element.value = elementValue
         }          
      })

      this.expandedSenses.push(false)
      this._render()
      this._validate()
   },

   _onSenseInput: function(sense, evt) {
      let origsenseElement = this._getChildElement(sense, "origsense")
      if (!origsenseElement) {
         origsenseElement = this._createChildElement(sense, "origsense")
         origsenseElement.value = sense.value
      }
      sense.value = evt.target.value
      this._validate()
   },

   _onHomonymNumberInput: function(sense, evt) {
      let homonymNumberElement = this._getOrCreateChildElement(sense, "homonym_number")
      homonymNumberElement.value = evt.target.value
      this._validate()
   },

   _onIsFigurativeChange: function(sense, evt) {
      let isFigurativeElement = this._getOrCreateChildElement(sense, evt.target.name)
      isFigurativeElement.value = evt.target.checked ? "1" : ""
      this._validate()
   },

   _onAddOwnExampleClick: function(evt) {
      $(evt.currentTarget).closest(".input-field")
            .find("input")
            .attr("disabled", null)
            .focus()
      this._validate()
   },

   _onSelectCandidateClick: function(sense, evt) {
      let example = $(evt.currentTarget).find(".candidateText").html().trim()
      let exampleElement = this._getOrCreateChildElement(sense, "example")
      let origExampleElement = this._getOrCreateChildElement(sense, "origexample")
      exampleElement.value = example
      origExampleElement.value = example
      this._render()
      this._validate()
   },

   _onCustomValueToggleClick(sense, evt){
       let inputField = $(evt.target).closest(".input-field")
       let customValueInput = inputField.find(".customValueInput")
       inputField.toggleClass("showCustomValueInput")
       if(inputField.hasClass("showCustomValueInput")){
          customValueInput .val(inputField.find(".select-wrapper input").val())
          customValueInput.get(0).focus()          
       } else {
          let element = this._getChildElement(sense, customValueInput.attr("name"))
          if(element){
             element.value = ""
          }
       }
   },

   _getMoreInformationForm: function(sense) {
      let register = this._getChildValue(sense, "register")
      let language = this._getChildValue(sense, "language")
      let field = this._getChildValue(sense, "field")
      let shorttype = this._getChildValue(sense, "shorttype")
      let scientificname = this._getChildValue(sense, "scientificname")
      let chemicalformula = this._getChildValue(sense, "chemicalformula")
      let registerOptions = this.const.register.map(r => `<option value="${r[0]}" ${r[0] == register ? 'selected' : ''}>${r[1]}</option>`).join("\n")
      let languageOptions = this.const.language.map(r => `<option value="${r[0]}" ${r[0] == language ? 'selected' : ''}>${r[1]}</option>`).join("\n")
      let fieldOptions = this.const.field.map(r => `<option value="${r[0]}" ${r[0] == field ? 'selected' : ''}>${r[1]}</option>`).join("\n")
      let shorttypeOptions = this.const.shorttype.map(r => `<option value="${r[0]}" ${r[0] == shorttype ? 'selected' : ''}>${r[1]}</option>`).join("\n")
      let isRegisterCustom = !Object.values(this.const.register).includes(register)
      return `
         <div>
            <div class="input-field selectWithCustomValue displayInlineFlex alignCenter ${isRegisterCustom ? 'showCustomValueInput' : ''}">
               <select name="register">
                  ${registerOptions}
               </select>
               <input type="text"
                     name="register"
                     class="customValueInput"
                     value="${register}"/>
               <i class="material-icons clickable customValueToggleIcon">edit</i>
               <label>${this._("register")}</label>
            </div>
         </div>
         <div>
            <div class="input-field selectWithCustomValue displayInlineFlex alignCenter">
               <select name="language">
                  ${languageOptions}
               </select>
               <input type="text"
                     name="language"
                     class="customValueInput"
                     value="${language}"/>
               <i class="material-icons clickable customValueToggleIcon">edit</i>
               <label>${this._("language")}</label>
            </div>
         </div>
         <div>
            <div class="input-field selectWithCustomValue displayInlineFlex alignCenter">
               <select name="field">
                  ${fieldOptions}
               </select>
               <input type="text"
                     name="field"
                     class="customValueInput"
                     value="${field}"/>
               <i class="material-icons clickable customValueToggleIcon">edit</i>
               <label>${this._("field")}</label>
            </div>
         </div>
         <div>
            <div class="input-field selectWithCustomValue displayInlineFlex alignCenter">
               <select name="shorttype">
                  ${shorttypeOptions}
               </select>
               <input type="text"
                     name="shorttype"
                     class="customValueInput"
                     value="${shorttype}"/>
               <i class="material-icons clickable customValueToggleIcon">edit</i>
               <label>${this._("shorttype")}</label>
            </div>
         </div>
         <div>
            <div class="input-field">
               <input name="scientificname"
                     value="${scientificname}">
               <label>
                  ${this._("scientificName")}
               </label>
            </div>
         </div>
         <div>
            <div class="input-field">
               <input name="chemicalformula"
                     value="${chemicalformula}">
               <label>
                  ${this._("chemicalFormula")}
               </label>
            </div>
         </div>`
   },

   _createCandidateListTemplate: function() {
      let candidates = this._getChildElements(this.entry, "candidate_example")
      this.exampleCandidateTemplate = candidates.map((candidate, idx) => {
         return `
         <div class="candidate displayFlex pointer alignCenter">
            <small class="num grey-text mr-2">${idx + 1}.</small>
            <span class="candidateText">
               ${candidate.value}
            </span>
         </div>`
      }).join("")
   },


   _onMoreInfoToggleClick: function(idx, evt) {
      this.expandedSenses[idx] = !this.expandedSenses[idx]
      $(evt.target).closest(".moreInfoContainer").find(".moreInfoForm").slideToggle()
   },


   _calculateExpandedSenseMoreInfo: function() {
      let senses = this._getChildElements(this.entry, "sense")
      this.expandedSenses = []
      senses.forEach((sense, idx) => {
         this.expandedSenses.push(!!(this._getChildValue(sense, "register") ||
            this._getChildValue(sense, "language") ||
            this._getChildValue(sense, "field") ||
            this._getChildValue(sense, "shorttype") ||
            this._getChildValue(sense, "scientificname") ||
            this._getChildValue(sense, "chemicalformula")))
      }, this)
   },

   _getChildElement: function(parent, childName) {
      return window.nvhStore.findElement(e => e.name == childName, parent)
   },

   _getOrCreateChildElement: function(parent, childName) {
      return this._getChildElement(parent, childName) || this._createChildElement(parent, childName)
   },

   _getChildElements: function(parent, childName) {
      return window.nvhStore.findElements(e => e.name == childName, parent)
   },

   _getChildValue: function(parent, childName, defaultValue = "") {
      let child = this._getChildElement(parent, childName)
      return child ? child.value : defaultValue
   },


   _createChildElement: function(parent, childElementName) {
      let child = {
         name: childElementName,
         value: "",
         parent: parent,
         children: []
      }
      parent.children.push(child)
      return child
   },

   _: function(id){
      return this.texts[this.lang][id] 
            || this.texts.en[id] 
            || id
   },

   _updateErrorMessage: function() {
      $("#errorMessage").html(this.error)
      $("#errorMessageBar").toggle(!!this.error)
   },

