   _validate: function() {
      let wasValid = this.isValid
      this.error = ""
      if (this.contentNode.find("#wrong_lemma").is(":checked")) {
         this.isValid = true
      } else {
         if ($(".myEditorContentNode #pronunciation").val() === "") {
            this.error = this._("errorPronunciation")
         } else if ($(".myEditorContentNode #syllabification").val() === "") {
            this.error = this._("errorSyllabification")
         }/* else if (!this._areHomonymNumbersValid()) {
            this.error = "TODO: homonym error."
         */ else if ($(".myEditorContentNode .senseDefinition input").filter(function() {
               return this.value == ""
            }).length) {
            this.error = this._("errorSenseDefinition")
         } else if ($(".myEditorContentNode .example input").filter(function() {
               return this.value == ""
            }).length) {
            this.error = this._("errorSenseExample")
         }
      }
      this.isValid = !this.error
      if (wasValid != this.isValid) {
         this.options.onValidChange(this.isValid)
      }
      this._updateErrorMessage()
   },

