editing.css:
.myEditorContentNode h1{
   text-transform: none
}
.errorMessageBar{
   position: sticky;
   top: 37px;
   z-index: 100;
}
.wrongLemmaWarning{
   text-align: center;
   margin-top: 10vh;
   color: lightgray;
   font-size: 50px;
   text-transform: uppercase;
}
.headwordForm .displayFlex{
   gap: 20px;
}
.headwordForm input[type="text"]{
   min-width: 220px;
}
.langToggle{
   position: absolute;
   right: 0;
   top: 0;
   cursor: pointer; 
   color: #000;
   font-weight: bold;
}
.langToggle span{
   color: grey;
   font-weight: normal;
}
.langToggle span:hove{
   text-decoration: underline;
}
.senseDefinition{
   /*max-width: 300px;*/
   flex: 1;
 }
.exampleInput{
   flex: 1;    
}
.homonym_number{
   max-width: 121px;
}
.figurativeCheckbox{
   min-width: 205px;
}
.senseNum{
   position: absolute;
   left: -25px;
   font-size: 20px;
   color: lightgray;
}
.candidateList{
   max-height: 117px;
   overflow: auto;
   border: 1px solid lightgrey;
}
.candidateList .num{
   min-width: 15px; 
   text-align: right;
}
.candidate:hover{
   background-color: #fff9c4;
}
.removeSenseBtn{
   position: absolute;
   right: -50px;
   top: 0px;
}
.moreInfoForm{
   display: flex;
   flex-wrap: wrap;
   gap: 20px;
}
.moreInfoForm > div{
   flex: 1 1 0px;
   min-width: 160px;
}
.moreInfoForm .customValueToggleIcon{
    border-radius: 50%;
    padding: 5px; 
    margin-left: 5px;   
}
.moreInfoForm .showCustomValueInput .customValueToggleIcon{
   background-color: #000000;
   color: white;
}
.moreInfoForm .selectWithCustomValue input{
   margin-bottom: 0;
}
.moreInfoForm .selectWithCustomValue:not(.showCustomValueInput) > input{
   display: none
}
.moreInfoForm .selectWithCustomValue.showCustomValueInput .select-wrapper{
   display: none
}
